function getApiInfo()
{
  global $db;
  $query = $db->prepare("SELECT * FROM pazaryeri_magaza WHERE  pazar_id = 2");
  $query->execute();
  $column = $query->fetch(PDO::FETCH_ASSOC);
  $apis = [
    'appKey' => $column['api_anahtar'],
    'appSecret' => $column['api_sifre'],
    'supplierId' => $column['api_id'],
    'cargoCompanyId' => $column["kargo"]
  ];
  return $apis;
}

